import React from 'react';
import Header from '../Header/Header';


class MyContacts extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isLoading: false,
            saveUpdate:false,
            first_name:'',
            contact_no:'',
            username:'',
            email:''


        }
    }

    componentDidMount(){
        const loggingIn = localStorage.getItem('user-token');
        const userInfo = JSON.parse(localStorage.getItem('userData'));

        this.setState({
            first_name:userInfo.first_name,
            contact_no:userInfo.contact_no,
            username:userInfo.username,
            email:userInfo.email
        });

        if(!loggingIn){
          this.props.history.push("/");
        }
     }

     handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

     handleSubmit = (event) =>{
        event.preventDefault();
        this.setState({ saveUpdate: true });
         alert('form submitted');
     }


       render() {

        const {userData,saveUpdate} = this.state;


         return (
            <>
            <Header />
                <div className="container">
                <div className="col-md-6 col-md-offset-3">

                <form name="form" onSubmit={this.handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="firstName">First Name</label>
                        <input type="text" className="form-control" name="first_name" value={userData.first_name} onChange={this.handleChange}/>


                    </div>
                    <div className='form-group'>
                        <label htmlFor="number">Contact Number</label>
                        <input type="number" className="form-control" name="contact_no" value={userData.contact_no} onChange={this.handleChange}/>

                     </div>
                    <div className='form-group'>
                        <label htmlFor="username">Username</label>
                        <input type="text" className="form-control" readOnly name="username" value={userData.username} />

                    </div>
                    <div className='form-group'>
                        <label htmlFor="email">Email</label>
                        <input type="text" className="form-control" name="email" value={userData.email} onChange={this.handleChange}/>



                    </div>

                    <div className="form-group">
                        <button className="btn btn-primary">Save</button>
                        {saveUpdate && 
                            <img src="data:image/gif;base64," />
                        }

                    </div>
                </form>
                </div>
              </div>
            </>
        );
    }
}

export default MyContacts;
