import React from 'react';
import { Link,withRouter } from 'react-router-dom';
import * as LoginAaction from '../../actions/loginAction';


import { connect } from 'react-redux';

class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        // reset login status
        this.props.logout();

        this.state = {
            username: '',
            password: '',
            submitted: false,
            loggingIn:false,

        };

    }
    componentDidMount(){
        const loggingIn = localStorage.getItem('user-token');
        if(loggingIn){
           this.setState({ 
               loggingIn: true
           });
        }
        else{
           this.setState({ 
               loggingIn: false
           }); 
        }

    }

    handleChange = (e) => {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.setState({ submitted: true});
        const { username, password } = this.state;

        if (username && password) {
          this.props.login(username, password);
          }
    }

    render() {
        const {loggingIn } = this.state;
        const { loggedIn,error} = this.props;
         if(loggingIn){
            this.props.history.push("/myhome");
         }
        const { username, password, submitted } = this.state;
        return (
            <div className="jumbotron">
                <div className="container">

                    <div className="col-md-6 col-md-offset-3">
                    {error && (<div className="alert alert-danger" role="alert">
                    {this.props.error}
                    </div>) }
                    <h2>Login</h2>
                        <form name="form" onSubmit={this.handleSubmit}>
                            <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                                <label htmlFor="username">Username  </label>
                                <input type="text" className="form-control" name="username" value={username} onChange={this.handleChange} />
                                {submitted && !username &&
                                    <div className="help-block">Username is required</div>
                                }
                            </div>
                            <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                                <label htmlFor="password">Password</label>
                                <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                                {submitted && !password &&
                                    <div className="help-block">Password is required</div>
                                }
                            </div>
                            <div className="form-group">
                                <button className="btn btn-primary">Login</button>
                                {loggedIn &&
                                    <img src="data:image/gif;base64," />
                                }
                                <Link to="/register" className="btn btn-link">Register</Link>
                            </div>

                        </form>
                        </div>
                    </div>
                </div>

        );
    }
}
const mapStateToProps = (state)=> {
    return {
        userinfo:state.loginReducer.currentUser,
        loggingIn:state.loginReducer.loggingIn, 
        loggedIn:state.loginReducer.loggedIn, 
        error:state.loginReducer.error
    };
}

export default withRouter(connect(mapStateToProps, { ...LoginAaction })(LoginPage));
